import React, { useState } from 'react';
import './Header.css';
import { useDispatch } from 'react-redux';
import { addLocationAction } from '../../actions/locationActions';


function Header({ getLocation }) {


  //component state
  const [city, setCity] = useState("medellín");
  // const [country, setCountry] = useState(undefined);
  // const [country, setCountry] = useState(undefined);

  //dispatch to start the actions
  const dispatch = useDispatch();
  const addNewLocation = city => dispatch(addLocationAction(city));

  const askAPI = async (e) => {
    
    const appid = '36e2fa16b70a4422ed609a5ad91f71f5';
    
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=${appid}&units=metric`;

    const response = await fetch(url);
    const result = await response.json();
    console.log(result)
  }

  const submitLocation = e => {    
    e.preventDefault();    
    // addNewLocation({
    //   city : city
    // })
    askAPI();
    setCity("")
  }

  return (
    <div className="App">
      <header>
        <div className="header">

          <ul className="headerList">
            <li>
              <form onSubmit={submitLocation}>
                <input
                  type="text"
                  id="search_input"
                  name="city"
                  placeholder="type your city"
                  aria-label="type a city to get weather and latest news"
                  
                  onChange={(e) => setCity(e.target.value)}
                />
                <button id="search_button" type="submit"><i className="fas fa-lg fa-search"></i></button>
              </form>
            </li>
          </ul>
        </div>
      </header>
    </div>
  );
}

export default Header;
