import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addCurrencyAction } from '../../actions/currencyActions'

function CurrencyCard() {
    const countryCode = useSelector(state => state.weather.weather[0] === undefined ? state.weather.weather[0] : state.weather.weather[0].country);
    const location = useSelector(state => state.location.location[0].location === undefined ? state.location.location[0] : state.location.location[0].location);
    console.log(countryCode);

    var request = require('request');

    var params = {
        'user-id': 'jorgatsu1',
        'api-key': '5I4qTF2JxCUR9pjFskDaJuQLoe8vJb1G2CWjRH6NNWb0EfEG',
        'ip': '162.209.104.195',
        'api-name': 'convert',
        'from-value': '1',
        'from-type': 'USD',
        'to-type': 'COP',
        'output-case': 'camel'
    };

    request.post(
        'https://www.neutrinoapi.com/account/tools/?api=convert?api-key=5I4qTF2JxCUR9pjFskDaJuQLoe8vJb1G2CWjRH6NNWb0EfEG?ip=162.209.104.195?from-value=1?from-type=USD?to-type:COP?output-case=camel',
        { form: params },
        function (error, response, body) {
            if (!error && response.statusCode == 200) {
                var result = JSON.parse(body);
                console.log(result['country']);
                console.log(result['country-code']);
                console.log(result['region']);
                console.log(result['city']);
            }
        }
    );

    return (
        <article>
            <header>
                <h2>Currency used in {location}</h2>
            </header>
            <div>
                <h3>$ 1 USD = $ 3.000,42 COP</h3>
                <h4>+ 0.25% â†‘</h4>
            </div>
        </article>
    );
}

export default CurrencyCard;
