import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addCurrencyAction } from '../../actions/currencyActions'

function CurrencyCard() {
    const countryCode = useSelector(state => state.weather.weather[0] === undefined ? state.weather.weather[0] : state.weather.weather[0].country);
    const location = useSelector(state => state.location.location[0].location === undefined ? state.location.location[0] : state.location.location[0].location);
    console.log(countryCode);
    var unirest = require("unirest");

    const req = ("https://fixer-fixer-currency-v1.p.rapidapi.com/convert");
    
    req.query({
        "from": "USD",
        "to": "ILS",
        "amount": "12"
    });
    
    req.headers({
        "x-rapidapi-host": "fixer-fixer-currency-v1.p.rapidapi.com",
        "x-rapidapi-key": "b6d1390605mshdf8aa24f282ddc6p13087bjsnb4b835881a2d"
    });
    
    
    req.end(function (res) {
        if (res.error) throw new Error(res.error);
    
        console.log(res.body);
    });
    
    return (
        <article>
            <header>
                <h2>Currency used in {location}</h2>
            </header>
            <div>
                <h3>$ 1 USD = $ 3.000,42 COP</h3>
                <h4>+ 0.25% â†‘</h4>
            </div>
        </article>
    );
}

export default CurrencyCard;
